sys_script.script:
// "rightnow" almacena la hora actual.
var rightnow = new GlideDateTime();
// Crear un objeto GlideDateTime para la fecha "Cuando se necesita".
var whenNeeded = new GlideDateTime(current.u_when_needed);

// Si la fecha "Cuando se necesita" es anterior a "rightnow", no escribir el registro en la base de datos.
// Mostrar un mensaje de error en la pantalla.
if(whenNeeded.before(rightnow)){
  gs.addErrorMessage("La fecha Cuando se necesita no puede estar en el pasado. Tu solicitud no ha sido guardada en la base de datos.");
  current.setAbortAction(true);
}
// Desafío: No permitir solicitudes para el mismo día.
// Obtener la parte de la fecha de "rightnow" y "whenNeeded" (sin la marca de tiempo).
var today = rightnow.getLocalDate();
var istoday = whenNeeded.getLocalDate();
// Comparar "today" e "istoday" para ver si son el mismo día.
if(today.compareTo(istoday) == 0){
  gs.addErrorMessage("  No es posible enviar solicitudes de NeedIt para el día de hoy.");
  current.setAbortAction(true);
  }
